$receiptTable=$("table#receipt-table");$receiptTable.on("change","input.product-name",function(){if((parseInt($(this).closest("tr").find("td.product-id").html()))!=$receiptTable.find("tr.product-record").length){return}$.ajax({type:"POST",url:"/transaction/new-row/",data:{rowNumber:$receiptTable.find("tr").length},success:function(a){$("table#receipt-table input.product-name").last().closest("tr").after(a)}})});$receiptTable.on("change","input.product-total-price",function(){var a=0;$("input.product-total-price").each(function(){var b=$(this).val().replace(/,/g,".");if(!isNaN(b)){a=Number(a)+Number(b)}});$("#totalPrice").html(parseFloat(Math.round(a*100)/100).toFixed(2))});